import React from "react";
import { useState } from "react";

const TodoList = () => {
    const [todoItems, setTodoItems] = useState({title: ''})
    const [addItem, setAddItem] = useState({})

    const addNewItem = (e) => {
        e.preventDefault()
        const newItems = {...todoItems, e}
    }


    return (
        <div>
            <div>
            <input
            value = {addItem.title}
            onChange = {e => setAddItem({...addItem, title: e.target.value })}
            type="text" 
            placeholder='Заметка' 
            />
            <button 
                onClick ={addNewItem}
            >Создать заметку</button>
            </div>
            <div>
               { todoItems.map(i => <li>{i}</li>)}
            </div>
        </div>
    )
}

export default TodoList
