import React from "react";
import { useState } from "react";
import  cl from './TodoList.module.css'


const TodoList = () => {
    const [todoItems, setTodoItems] = useState([])
    const [addItem, setAddItem] = useState({title : '', id : ''})
    let checkbox 

   

    const addNewItem = (e) => {
        e.preventDefault();
        setTodoItems([...todoItems, addItem])
        setAddItem({title : '', id : ''})
    }

    const delItem = (e) => {
        setTodoItems(todoItems.filter(item => (String(item.id) !== String(e.target.id))))
    }

    return (
        <div>
            <form onSubmit={addNewItem}>
                <input
                    value={addItem.title}
                    type="text" 
                    placeholder='Заметка' 
                    onChange = {e => setAddItem({...addItem, title: e.target.value})}
                />
                <button 
                type="submit"
                onClick = {() => setAddItem({...addItem, id: Date.now()})}
                >Создать заметку
                </button>
            </form>
            <ul>{todoItems.map(item => (
                    <li className = {cl.checkbox}>
                        <input 
                        type="checkbox"
                        onClick = {(e) => e.target.checked? '' : ''}
                        >                      
                        </input>
                        {item.title}
                        <button id = {item.id}
                        onClick={delItem}
                        >Удалить</button>
                    </li>
                ))}
            </ul>
        </div>
    )
}

export default TodoList
