import React from "react";
import { useState } from "react";


const TodoList = () => {
    const [todoItems, setTodoItems] = useState([])
    const [addItem, setAddItem] = useState({title : ''})

    const addNewItem = (e) => {
        e.preventDefault();
        setTodoItems([...todoItems, addItem])
        setAddItem()
        console.log(addItem)
    }

    return (
        <div>
            <form onSubmit={addNewItem}
            >
                <input
                    value={addItem.title}
                    type="text" 
                    placeholder='Заметка' 
                    onChange = {e => setAddItem({...addItem, title: e.target.value })}
                />
                <button 
                type="submit"
                
                >
                Создать заметку
                </button>
            </form>
            <ul>{todoItems.map(item => (<li> {item.title} </li>))}</ul>
        </div>
    )
}

export default TodoList
