import React from "react";
import { useState } from "react";

const TodoList = () => {
    const [todoItems, setTodoItems] = useState([])
    const [addItem, setAddItem] = useState({title: ''})

    const addNewItem = (e) => {
        e.preventDefault()
        setTodoItems([...todoItems, addItem])
        setAddItem({title: ''})
        console.log(todoItems)
    }

    


    return (
        <div>
            <div>
            <input
            value = {addItem.title}
            onChange = {e => setAddItem({...addItem, title: e.target.value })}
            type="text" 
            placeholder='Заметка' 
            />
            <button 
                onClick ={addNewItem}
            >Создать заметку</button>
            </div>
            <div>
               {todoItems.map(i => {<li>{i.title}</li>})}
            </div>
        </div>
    )
}

export default TodoList
