import React from "react";
import ReactDOM  from "react-dom";
import { useState } from "react";


const TodoList = () => {
    const [todoItems, setTodoItems] = useState([])
    const [addItem, setAddItem] = useState({title : '', id : ''})

   

    const addNewItem = (e) => {
        e.preventDefault();
        // setAddItem({id : todoItems.length})
        setTodoItems([...todoItems, addItem])
        setAddItem({title : '', id : ''})
        console.log(todoItems)
    }

    const delItem = (e) => {
        // setTodoItems(todoItems.filter((item , index)=> console.log(item)));
        // console.log(todoItems.filter(item => item.title !== e.target.title))
        console.log(e.target.id)
        // todoItems.filter((item, index, array) => console.log(item.id))
        setTodoItems(todoItems.filter(p => (p.id !== elem.id)))
        // elem.target.remove(elem)
  
    }

    return (
        <div>
            <form onSubmit={addNewItem}>
                <input
                    value={addItem.title}
                    type="text" 
                    placeholder='Заметка' 
                    onChange = {e => setAddItem({...addItem, title: e.target.value})}
                />
                <button type="submit"
                onClick = {() => setAddItem({...addItem, id: todoItems.length + 1})}
                >Создать заметку
                </button>
            </form>
            <ul>{todoItems.map((item, index) => (
                    <li id = {index}>
                        <input type="checkbox"></input>
                        {item.title}
                        <button id = {index}
                        onClick={delItem}
                        >Удалить</button>
                    </li>
                ))}
            </ul>
        </div>
    )
}

export default TodoList
