import React from "react";
import { useState } from "react";


const TodoList = () => {
    const [todoItems, setTodoItems] = useState([])
    const [addItem, setAddItem] = useState([])

    const addNewItem = (newItem) => {
        newItem.preventDefault()
        setAddItem([...addItem, newItem])
        setAddItem({title: ''})
        console.log(addItem)
    }

    const visable = () =>{
        todoItems.map(i => {<li>{i.title}</li>})
    }


    return (
        <div>
            <div>
                <form action="">
                <input
                value={addItem.title}
                onChange = {e => setAddItem({...addItem, title: e.target.value })}
                type="text" 
                placeholder='Заметка' 
                />
                <button onClick ={addNewItem}>Создать заметку</button>
                </form>
            </div>
            <div>
            
            </div>
        </div>
    )
}

export default TodoList
