import React from "react";
import ReactDOM  from "react-dom";
import { useState } from "react";


const TodoList = () => {
    const [todoItems, setTodoItems] = useState([])
    const [addItem, setAddItem] = useState({title : ''})

    const addNewItem = (e) => {
        e.preventDefault();
        setTodoItems([...todoItems, addItem])
        setAddItem({title : ''})
        console.log(addItem)
    }

    const delItem = (e) => {

        
        const newArray = todoItems.filter.call(todoItems, (todo) => (e.target.id === todo.id))
        // newArray.remove()
        newArray.remove()
        setTodoItems([])
        setTodoItems([...todoItems, newArray])
    }

    return (
        <div>
            <form onSubmit={addNewItem}>
                <input
                    value={addItem.title}
                    type="text" 
                    placeholder='Заметка' 
                    onChange = {e => setAddItem({...addItem, title: e.target.value })}
                />
                <button type="submit">Создать заметку</button>
            </form>
            <ul>{todoItems.map((item, index) => (
                    <li id={index} >
                        <input type="checkbox"></input>
                        {item.title}
                        <button
                        onClick={(e, index) => delItem(e, index)}
                        >Удалить</button>
                    </li>
                ))}
            </ul>
        </div>
    )
}

export default TodoList
