import React from "react";

export const TodoItem = ({ id, title, isChecked, setTodoItems }) => {

  const handleCheckboxChange = () => {
    setTodoItems((prevState) => {
      const newState = prevState.map((todoItem) => {
        if (todoItem.id !== id) {
          return todoItem 
        } else {
          return {
            id: todoItem.id,
            title: todoItem.title,
            isChecked: !todoItem.isChecked
          }
        }
      })
      return newState
    })
  }

  return (
    <li>
    <div>
      <input checked={isChecked} type="checkbox" onClick={handleCheckboxChange} />
      {title}
    </div>
    <button onClick={''}>
      Delete
    </button>
  </li> 
  );
};
