import React from "react";
import { useState } from "react";

const TodoList = () => {
    const [todoItems, setTodoItems] = useState([])
    const [addItem, setAddItem] = useState({})

    const addNewItem = (e) => {
        e.preventDefault()
        const newItem = {
            ...todoItems, id: Date.now()
        }
        setTodoItems(newItem)
        setAddItem({title : ''})
    }


    return (
        <div>
            <div>
            <input
            value = {addItem.title}
            onChange = {e => setAddItem({...addItem, title: e.target.value })}
            type="text" 
            placeholder='Заметка' 
            />
            <button 
                onClick ={addNewItem}
            >Создать заметку</button>
            </div>
            <div>
            
            </div>
        </div>
    )
}

export default TodoList
