        console.log(addItem)
    }

    const delItem = (e) => {
        setTodoItems(todoItems.filter((item , index)=> console.log(index)));
        // console.log(todoItems.filter(item => item.id !== e.target.id))
        // console.log(item)

        // setTodoItems(todoItems.filter(p => (p.id !== elem.id)))
        // elem.target.remove(elem)
  
    }

    return (
        <div>
            <form onSubmit={addNewItem}>
                <input
                    value={addItem.title}
                    type="text" 
                    placeholder='Заметка' 
                    onChange = {e => setAddItem({...addItem, title: e.target.value })}
                />
                <button type="submit">Создать заметку</button>
            </form>
            <ul>{todoItems.map((item, index) => (
                    <li>
                        <input type="checkbox"></input>
                        {item.title}
                        <button id = {index}
                        onClick={delItem}
                        >Удалить</button>
                    </li>
                ))}
            </ul>
        </div>
    )
}

export default TodoList
