import React from "react";
import { useState } from "react";


const TodoList = () => {
    const [todoItems, setTodoItems] = useState([])
    const [addItem, setAddItem] = useState('')
    const x = () => (x =+0)

    const addNewItem = (e) => {
        e.preventDefault()
        setTodoItems([...todoItems, addItem])
        setAddItem('')
        x()
        // console.log(todoItems)
        return (todoItems.map( item => <li key={x =+1}> {item.title} </li>))
    }

    return (
        <div>
            <form action="">
                <input
                    value={addItem.title}
                    onChange = {e => setAddItem({...addItem, title: e.target.value })}
                    type="text" 
                    placeholder='Заметка' 
                    />
                <button onClick = {addNewItem()}>Создать заметку</button>
                </form>
                <ul>{addNewItem}</ul>
        </div>
    )
}

export default TodoList
