    const addNewItem = (newItem) => {
        newItem.preventDefault()
        setTodoItems([...todoItems, newItem])
        setAddItem({title: ''})
        console.log(todoItems)
    }

    const visable = () =>{
        todoItems.map(i => {<li>{i.title}</li>})
    }


    return (
        <div>
            <div>
                <input
                value = {addItem.title}
                onChange = {e => setAddItem({...addItem, title: e.target.value })}
                type="text" 
                placeholder='Заметка' 
