import React from "react";
import { useState } from "react";

const TodoList = () => {
    const [todoItems, setTodoItems] = useState([])
    const [addItem, setAddItem] = useState({title: ''})

    const addNewItem = (e) => {
        e.preventDefault()
        setTodoItems([...todoItems, addItem])
        setAddItem({title: ''})
    }

    const visable = () =>{
        todoItems.map(i => {<li>{i.title}</li>})
    }


    return (
        <div>
            <div>
            <input
                value = {addItem.title}
                onChange = {e => setAddItem({...addItem, title: e.target.value })}
                type="text" 
                placeholder='Заметка' 
                />
                <button 
                    onClick ={addNewItem}
                    >
                    Создать заметку
                </button>
            </div>
            <div>
            </div>
        </div>
    )
}

export default TodoList
