import React from "react";
import { useState } from "react";


const TodoList = () => {
    const [todoItems, setTodoItems] = useState([])
    const [addItem, setAddItem] = useState({})

    const addNewItem = (e) => {
        // e.preventDefault();
        setTodoItems([...todoItems, addItem])
        setAddItem({})
        console.log(todoItems)
        return (todoItems.map( item => (<li> {item.title} </li>)))
    }

    return (
        <div>
            <form 
            action=""
            // onSubmit={addNewItem()}
            >
                <input
                    value={addItem.title}
                    
                    type="text" 
                    placeholder='Заметка' 
                    />
                <button 
                type="submit"
                onClick = {e => setAddItem({...addItem, title: e.target.value })}
                >Создать заметку</button>
            </form>
            <ul>{addNewItem}</ul>
        </div>
    )
}

export default TodoList
