import React from "react";
import { useState } from "react";


const TodoList = () => {
    const [todoItems, setTodoItems] = useState(['d',])
    const [addItem, setAddItem] = useState([])

    const addNewItem = (e) => {
        e.preventDefault()
        const newItem = {
            id: Date.now(),
            addItem
        } 
        setTodoItems([...todoItems, newItem])
        console.log(todoItems)
    }

    return (
        <div>
            <div>
                <form action="">
                    <input
                    value={addItem.title}
                    onChange = {e => setAddItem({...addItem, title: e.target.value })}
                    type="text" 
                    placeholder='Заметка' 
                    />
                    <button onClick = {addNewItem}>Создать заметку</button>
                </form>
                <ul>{todoItems.map(name => <li key={name}> {name} </li>)}</ul>
            </div>
            <div>
            
            </div>
        </div>
    )
}

export default TodoList
